import React, { useState } from 'react';
import firebaseApp from '../services/firebase-config';
import { getAuth, signInWithEmailAndPassword } from 'firebase/auth';
import '../styles/auth_style';

function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [isLoggedIn, setIsLoggedIn] = useState(false); // State to check login status
  const auth = getAuth(firebaseApp);

  const handleLogin = (e) => {
    e.preventDefault();
    signInWithEmailAndPassword(auth, email, password)
      .then((userCredential) => {
        console.log('User logged in:', userCredential.user);
        setIsLoggedIn(true); // Set the login status to true after successful login
      })
      .catch((error) => {
        console.error('Error logging in:', error);
      });
  };

  // Render the welcome message if the user is logged in
  if (isLoggedIn) { 
    return (
      <div className="welcome-container">
        <h1 className="welcome-message">Welcome to the Shared board!</h1>
      </div>
    );
  }

  // Render the login form if the user is not logged in
  return (
    <div className="wrapper">
      <h2>Login</h2>
      <form onSubmit={handleLogin}>
        <div className="input-field">
          <input
            type="email"
            id="email"
            required
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            placeholder=" "
          />
          <label htmlFor="email">Email</label>
        </div>
        <div className="input-field">
          <input
            type="password"
            id="password"
            required
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder=" "
          />
          <label htmlFor="password">Password</label>
        </div>
        <button type="submit">Login</button>
      </form>
    </div>
  );
}

export default Login;
